const modal=document.getElementById("img-modal"),modalImg=document.getElementById("modal-img"),closeBtn=document.getElementById("modal-close"),images=document.querySelectorAll(".section img");function closeModal(){modal.classList.remove("show"),setTimeout(()=>{modal.style.display="none"},250)}images.forEach(e=>{e.addEventListener("click",()=>{modalImg.src=e.src,modal.style.display="block",requestAnimationFrame(()=>{modal.classList.add("show")})})}),closeBtn.addEventListener("click",closeModal),modal.addEventListener("click",e=>{e.target===modal&&closeModal()}),document.addEventListener("keydown",e=>{"Escape"===e.key&&closeModal()});
